(39, 1, '2021-09-16 20:03:56', '2021-09-16 23:03:56', '<!-- wp:latest-posts {\"categories\":[{\"id\":6,\"count\":1,\"description\":\"\",\"link\":\"http://localhost/wordpress/category/noticias/\",\"name\":\"Noticías\",\"slug\":\"noticias\",\"taxonomy\":\"category\",\"parent\":0,\"meta\":[],\"_links\":{\"self\":[{\"href\":\"http://localhost/wordpress/wp-json/wp/v2/categories/6\"}],\"collection\":[{\"href\":\"http://localhost/wordpress/wp-json/wp/v2/categories\"}],\"about\":[{\"href\":\"http://localhost/wordpress/wp-json/wp/v2/taxonomies/category\"}],\"wp:post_type\":[{\"href\":\"http://localhost/wordpress/wp-json/wp/v2/posts?categories=6\"}],\"curies\":[{\"name\":\"wp\",\"href\":\"https://api.w.org/{rel}\",\"templated\":true}]}}],\"displayPostContent\":true,\"displayFeaturedImage\":true} /-->', 'Noticias', '', 'trash', 'closed', 'closed', '', 'noticias__trashed', '', '', '2021-09-23 19:20:58', '2021-09-23 22:20:58', '', 0, 'http://localhost/wordpress/?page_id=39', 0, 'page', '', 0),
